class ToggleFollow{constructor(l){this.toggler=l,this.toggleFollow()}toggleFollow(){$(this.toggler).click((function(l){l.preventDefault();let o=this;$.ajax({type:"POST",url:$(o).attr("href")}).done((function(l){let t=parseInt($(".follower").attr("follows"));1==l.data.deleted?($(o).html("Follow"),t-=1):(t+=1,$(o).html("Unfollow")),$(".follower").attr("follows",t),$(".follower").html(`<b>${t}</b> Followers`)})).fail((function(l){console.log("Error in completing the request")}))}))}}