class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-likes"));1==t.data.deleted?(a-=1,$(e).html(`<i class="far fa-heart red"></i> ${a} Likes`)):(a+=1,$(e).html(`<i class="fas fa-heart red"></i> ${a} Likes`)),$(e).attr("data-likes",a)})).fail((function(t){console.log("Error in completing the request")}))}))}}